import React from "react";

import "./InvestmentForm.css";
import InvestmentInputGroup from "./InvestmentInputGroup";
import InvestmentInput from "./InvestmentInput";
import InvestmentButton from "./InvestmentButton";

function InvestmentForm(props) {
  return (
    <form className="form">
      <InvestmentInputGroup>
        <InvestmentInput id="current-savings">
          Current Savings ($)
        </InvestmentInput>
        <InvestmentInput id="yearly-contribution">
          Yearly Savings ($)
        </InvestmentInput>
      </InvestmentInputGroup>
      <InvestmentInputGroup>
        <InvestmentInput id="expected-return">
          Expected Interest (%, per year)
        </InvestmentInput>
        <InvestmentInput id="duration">
          Investment Duration (years)
        </InvestmentInput>
      </InvestmentInputGroup>
      <p className="actions">
        <button type="reset">
          Reset
        </button>
        <InvestmentButton type="submit">
          Calculate
        </InvestmentButton>
      </p>
    </form>
  );
}

export default InvestmentForm;
